var gulp = require('gulp'),
	uitIndex = require('../');

gulp.task('default',function(){
	gulp.src(['./**/*.html','./**/*.php'])
	.pipe(uitIndex())
	.pipe(gulp.dest('./'));
});

gulp.task('js',function(){
	gulp.src(['./**/*.html','./**/*.php'])
	.pipe(uitIndex({
		exJs: true
	}))
	.pipe(gulp.dest('./'));
});